const notEs5 = require( 'eslint-config-wikimedia/language/not-es5' );

// Disable rule for getOwnPropertySymbols as this is generated by
// webpack, but try to run the remainder of not-es5 to catch any
// ES6+ code.
// Were it not for this exception, we could just extend wikimedia/language/es5
notEs5.rules[ 'no-restricted-properties' ] =
	notEs5.rules[ 'no-restricted-properties' ].filter( ( rule ) =>
		rule.property !== 'getOwnPropertySymbols'
	);

module.exports = {
	root: true,
	parserOptions: {
		ecmaVersion: 5
	},
	rules: notEs5.rules
};
